content_all = ''
for url in urls:
    response = requests.get(url)
    soup = BeautifulSoup(response.text, 'html.parser')

    div_chapter_in4 = soup.find('div', {'id':'h1'})
    chapter_title = div_chapter_in4. find('h1').get_text()

    div_content = soup.find('div', {'id':'txtContent'})
    content = div_content.get_text(separator='\n')

    content_all += chapter_title.strip() + '\n\n' + content.strip() + '\n\n\n'
    print("Done", chapter_title)
with open('novel.txt', 'w', encoding='utf-8') as f:
    f.write(content_all)